import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { ArrowLeftIcon, DocumentIcon, XIcon } from '@heroicons/react/outline';
import { PaperClipIcon } from '@heroicons/react/solid';
import { useDropzone } from 'react-dropzone';

interface LabTestResult {
  name: string;
  value: number;
  unit: string;
  referenceRangeLow?: number;
  referenceRangeHigh?: number;
  isAbnormal?: boolean;
}

interface LabTestFormData {
  type: string;
  name: string;
  provider: string;
  date: string;
  results: LabTestResult[];
  notes?: string;
}

const LabTestUpload: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [file, setFile] = useState<File | null>(null);
  const [uploadedFileUrl, setUploadedFileUrl] = useState<string | null>(null);
  const [formData, setFormData] = useState<LabTestFormData>({
    type: 'blood',
    name: '',
    provider: '',
    date: new Date().toISOString().split('T')[0],
    results: [{ name: '', value: 0, unit: '' }],
    notes: ''
  });
  const [error, setError] = useState<string | null>(null);

  const onDrop = (acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0) {
      setFile(acceptedFiles[0]);
      // In a real application, you would upload the file to your server here
      // For now, we'll simulate file upload
      setTimeout(() => {
        setUploadedFileUrl('https://example.com/lab-report.pdf');
      }, 1000);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'image/jpeg': ['.jpg', '.jpeg'],
      'image/png': ['.png']
    },
    maxFiles: 1
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleResultChange = (index: number, field: keyof LabTestResult, value: string | number | boolean | undefined) => {
    const updatedResults = [...formData.results];
    updatedResults[index] = { ...updatedResults[index], [field]: value };
    setFormData({ ...formData, results: updatedResults });
  };

  const addResult = () => {
    setFormData(prev => ({
      ...prev,
      results: [...prev.results, { name: '', value: 0, unit: '' }]
    }));
  };

  const removeResult = (index: number) => {
    if (formData.results.length > 1) {
      const updatedResults = formData.results.filter((_, i) => i !== index);
      setFormData(prev => ({ ...prev, results: updatedResults }));
    }
  };

  const removeFile = () => {
    setFile(null);
    setUploadedFileUrl(null);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name || !formData.provider || !formData.date) {
      setError('Please fill in all required fields');
      return;
    }

    // Validate results
    const invalidResults = formData.results.some(r => !r.name || !r.unit);
    if (invalidResults) {
      setError('Please fill in all result fields');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      // Create lab test
      const testData = {
        ...formData,
        rawFileUrl: uploadedFileUrl
      };

      const response = await axios.post('/api/lab-tests', testData);

      if (response.data.success) {
        navigate(`/lab-tests/${response.data.data._id}`);
      } else {
        throw new Error(response.data.message || 'Failed to create lab test');
      }
    } catch (err: any) {
      setError(err.response?.data?.message || err.message || 'Failed to create lab test');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center">
        <Link to="/lab-tests" className="mr-4">
          <ArrowLeftIcon className="h-5 w-5 text-gray-500 hover:text-gray-700" />
        </Link>
        <h1 className="text-2xl font-semibold text-gray-900">Upload Lab Test</h1>
      </div>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <XIcon className="h-5 w-5 text-red-400" />
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">{error}</p>
            </div>
          </div>
        </div>
      )}

      <div className="bg-white shadow overflow-hidden rounded-lg">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg font-medium text-gray-900">Lab Test Information</h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            Fill in the details of your lab test
          </p>
        </div>
        <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="name" className="form-label">Test Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="Complete Blood Count, Lipid Panel, etc."
                  required
                />
              </div>

              <div>
                <label htmlFor="type" className="form-label">Test Type</label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  className="form-select"
                  required
                >
                  <option value="blood">Blood</option>
                  <option value="dna">DNA</option>
                  <option value="rna">RNA</option>
                  <option value="hormone">Hormone</option>
                  <option value="microbiome">Microbiome</option>
                  <option value="other">Other</option>
                </select>
              </div>

              <div>
                <label htmlFor="provider" className="form-label">Provider</label>
                <input
                  type="text"
                  id="provider"
                  name="provider"
                  value={formData.provider}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="LabCorp, Quest, etc."
                  required
                />
              </div>

              <div>
                <label htmlFor="date" className="form-label">Test Date</label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>
            </div>

            <div className="mt-6">
              <label className="form-label">Upload Lab Report (PDF or Image)</label>
              <div
                {...getRootProps()}
                className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-dashed rounded-md ${
                  isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'
                }`}
              >
                <div className="space-y-1 text-center">
                  <input {...getInputProps()} />
                  {file ? (
                    <div className="flex flex-col items-center">
                      <DocumentIcon className="h-12 w-12 text-blue-500" />
                      <p className="text-sm text-gray-700 mt-2">{file.name}</p>
                      <button
                        type="button"
                        onClick={removeFile}
                        className="mt-2 text-sm text-red-600 hover:text-red-800"
                      >
                        Remove file
                      </button>
                    </div>
                  ) : (
                    <>
                      <PaperClipIcon className="mx-auto h-12 w-12 text-gray-400" />
                      <p className="text-sm text-gray-600">
                        <span className="font-medium text-blue-600 hover:text-blue-500">
                          Click to upload
                        </span>{' '}
                        or drag and drop
                      </p>
                      <p className="text-xs text-gray-500">PDF, PNG, JPG up to 10MB</p>
                    </>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6">
              <div className="flex items-center justify-between mb-4">
                <label className="form-label">Test Results</label>
                <button
                  type="button"
                  onClick={addResult}
                  className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none"
                >
                  Add Result
                </button>
              </div>

              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Biomarker
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Value
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Unit
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Reference Low
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Reference High
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Abnormal
                      </th>
                      <th scope="col" className="relative px-6 py-3">
                        <span className="sr-only">Action</span>
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {formData.results.map((result, index) => (
                      <tr key={index}>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="text"
                            value={result.name}
                            onChange={(e) => handleResultChange(index, 'name', e.target.value)}
                            className="form-input"
                            placeholder="e.g. Vitamin D"
                            required
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="number"
                            value={result.value}
                            onChange={(e) => handleResultChange(index, 'value', parseFloat(e.target.value))}
                            className="form-input"
                            step="0.01"
                            required
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="text"
                            value={result.unit}
                            onChange={(e) => handleResultChange(index, 'unit', e.target.value)}
                            className="form-input"
                            placeholder="e.g. ng/mL"
                            required
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="number"
                            value={result.referenceRangeLow || ''}
                            onChange={(e) => handleResultChange(index, 'referenceRangeLow', e.target.value ? parseFloat(e.target.value) : undefined)}
                            className="form-input"
                            step="0.01"
                            placeholder="Optional"
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="number"
                            value={result.referenceRangeHigh || ''}
                            onChange={(e) => handleResultChange(index, 'referenceRangeHigh', e.target.value ? parseFloat(e.target.value) : undefined)}
                            className="form-input"
                            step="0.01"
                            placeholder="Optional"
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="checkbox"
                            checked={result.isAbnormal || false}
                            onChange={(e) => handleResultChange(index, 'isAbnormal', e.target.checked)}
                            className="form-checkbox"
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          {formData.results.length > 1 && (
                            <button
                              type="button"
                              onClick={() => removeResult(index)}
                              className="text-red-600 hover:text-red-900"
                            >
                              Remove
                            </button>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            <div className="mt-6">
              <label htmlFor="notes" className="form-label">Notes</label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes || ''}
                onChange={handleChange}
                rows={3}
                className="form-textarea"
                placeholder="Any additional notes about this test"
              ></textarea>
            </div>

            <div className="flex justify-end space-x-3">
              <Link
                to="/lab-tests"
                className="btn btn-gray"
              >
                Cancel
              </Link>
              <button
                type="submit"
                disabled={loading}
                className="btn btn-primary"
              >
                {loading ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Saving...
                  </>
                ) : (
                  'Save Lab Test'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LabTestUpload;
