import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { 
  HomeIcon as HomeIconOutline, 
  BeakerIcon as BeakerIconOutline, 
  CubeIcon as CubeIconOutline, 
  UserIcon as UserIconOutline, 
  ChatBubbleLeftRightIcon as ChatBubbleLeftRightIconOutline
} from '@heroicons/react/24/outline';
import {
  HomeIcon, 
  BeakerIcon, 
  CubeIcon, 
  UserIcon, 
  ChatBubbleLeftRightIcon
} from '@heroicons/react/24/solid';

interface BottomNavProps {
  isPremium: boolean;
}

interface NavItemProps {
  to: string;
  label: string;
  activeIcon: React.ReactNode;
  inactiveIcon: React.ReactNode;
  disabled?: boolean;
}

const NavItem: React.FC<NavItemProps> = ({ to, label, activeIcon, inactiveIcon, disabled = false }) => {
  const location = useLocation();
  const isActive = location.pathname === to || location.pathname.startsWith(`${to}/`);

  if (disabled) {
    return (
      <div className="bottom-nav-item opacity-60">
        <div className="bottom-nav-icon text-gray-400">{inactiveIcon}</div>
        <span>{label}</span>
      </div>
    );
  }

  return (
    <NavLink 
      to={to} 
      className={({ isActive }) => 
        `bottom-nav-item ${isActive ? 'text-blue-600' : 'text-gray-500'}`
      }
    >
      <div className="bottom-nav-icon">
        {isActive ? activeIcon : inactiveIcon}
      </div>
      <span>{label}</span>
    </NavLink>
  );
};

const BottomNav: React.FC<BottomNavProps> = ({ isPremium }) => {
  return (
    <div className="bottom-nav z-20">
      <NavItem
        to="/"
        label="Home"
        activeIcon={<HomeIconSolid className="h-6 w-6" />}
        inactiveIcon={<HomeIconOutline className="h-6 w-6" />}
      />
      <NavItem
        to="/lab-tests"
        label="Labs"
        activeIcon={<BeakerIconSolid className="h-6 w-6" />}
        inactiveIcon={<BeakerIconOutline className="h-6 w-6" />}
      />
      <NavItem
        to="/supplements"
        label="Supplements"
        activeIcon={<CubeIconSolid className="h-6 w-6" />}
        inactiveIcon={<CubeIconOutline className="h-6 w-6" />}
      />
      <NavItem
        to="/health-assistant"
        label="Assistant"
        activeIcon={<ChatIconSolid className="h-6 w-6" />}
        inactiveIcon={<ChatBubbleLeftRightIconOutline className="h-6 w-6" />}
        disabled={!isPremium}
      />
      <NavItem
        to="/account"
        label="Profile"
        activeIcon={<UserIconSolid className="h-6 w-6" />}
        inactiveIcon={<UserIconOutline className="h-6 w-6" />}
      />
    </div>
  );
};

export default BottomNav;
